			database.AddInParameter(commandWrapper, "@EdCdeExmptId", DbType.Int32, entity.EdCdeExmptId );
			database.AddInParameter(commandWrapper, "@DeptApproveDt", DbType.Date, (entity.DeptApproveDt.HasValue ? (object) entity.DeptApproveDt : System.DBNull.Value) );
			database.AddInParameter(commandWrapper, "@FacApproveDt", DbType.Date, (entity.FacApproveDt.HasValue ? (object) entity.FacApproveDt : System.DBNull.Value) );
			database.AddInParameter(commandWrapper, "@UnivApproveDt", DbType.Date, (entity.UnivApproveDt.HasValue ? (object) entity.UnivApproveDt : System.DBNull.Value) );
			database.AddInParameter(commandWrapper, "@ApproveFlg", DbType.Decimal, entity.ApproveFlg );
			database.AddInParameter(commandWrapper, "@ExmptApproveDt", DbType.DateTime, (entity.ExmptApproveDt.HasValue ? (object) entity.ExmptApproveDt : System.DBNull.Value) );
			database.AddInParameter(commandWrapper, "@ExmptApprvUsrId", DbType.Decimal, (entity.ExmptApprvUsrId.HasValue ? (object) entity.ExmptApprvUsrId : System.DBNull.Value) );
			
			int results = 0;
			
			//Provider Data Requesting Command Event
			OnDataRequesting(new CommandEventArgs(commandWrapper, "Update", entity));

			if (transactionManager != null)
			{
				results = Utility.ExecuteNonQuery(transactionManager, commandWrapper);
			}
			else
			{
				results = Utility.ExecuteNonQuery(database,commandWrapper);
			}
			
			//Stop Tracking Now that it has been updated and persisted.
			if (DataRepository.Provider.EnableEntityTracking)
				EntityManager.StopTracking(entity.EntityTrackingKey);
			
			entity.OriginalEdStudExmptReqId = entity.EdStudExmptReqId;
			
			entity.AcceptChanges();
			
			//Provider Data Requested Command Event
			OnDataRequested(new CommandEventArgs(commandWrapper, "Update", entity));

			return Convert.ToBoolean(results);
		}
			
